<script setup lang="uts">
  type Formats = {
    bold?: boolean
    italic?: boolean
    underline?: boolean
    strike?: boolean
    align?: string
    lineHeight?: boolean
    letterSpacing?: boolean
    marginTop?: boolean
    marginBottom?: boolean
    fontFamily?: boolean
    fontSize?: string
    color?: string
    backgroundColor?: string
    list?: string
    header?: number
    script?: string
    direction?: string
  }

  type DataType = {
    readOnly: boolean;
    formats: Formats;
    // TODO 补充editorContext类型
    editorCtx: any;
    // 自动化测试
    autoTest: boolean;
    undoTest: boolean;
    redoTest: boolean;
    removeFormatTest: boolean;
    insertImageTest: boolean;
    blurTest: boolean;
  }
  // 使用reactive避免ref数据在自动化测试中无法访问
  const data = reactive({
    readOnly: false,
    formats: {
      bold: false,
      italic: false,
      underline: false,
      strike: false,
      align: '',
      lineHeight: false,
      letterSpacing: false,
      marginTop: false,
      marginBottom: false,
      fontFamily: false,
      fontSize: '',
      color: '',
      backgroundColor: '',
      list: '',
      header: 0,
      script: '',
      direction: ''
    },
    // TODO 补充editorContext类型
    editorCtx: {},
    // 自动化测试
    autoTest: false,
    undoTest: false,
    redoTest: false,
    removeFormatTest: false,
    insertImageTest: false,
    blurTest: false
  } as DataType)

  onReady(() => {
    uni.loadFontFace({
      family: 'Pacifico',
      source: 'url("/static/font/Pacifico-Regular.ttf")',
      success() {
        console.log('success load font')
      },
      fail() {
        console.log('fail load font load')
      }
    })
  })

  const readOnlyChange = () => {
    data.readOnly = !data.readOnly
  }

  const onEditorReady = () => {
    uni.createSelectorQuery().select('#editor').context((res) => {
      data.editorCtx = res.context
    }).exec()
  }

  // 自动化测试专用
  const setContents = (options: any) => {
    data.editorCtx.setContents({
      delta: {
        ops: options
      },
      success: (res: any) => {
        console.log('setContents-success', res)
      },
      fail: (err: any) => {
        console.log(err)
      }
    })
  }

  const blur = () => {
    data.editorCtx.blur({
      success: (res: any) => {
        console.log('编辑器失焦：', res)
        data.blurTest = true
      },
      fail: (err: any) => {
        console.log(err)
      }
    })
  }

  const getCon = () => {
    data.editorCtx.getContents({
      success: (res: any) => {
        console.log('文本详情：', res)
      },
      fail: (err: any) => {
        console.log(err)
      }
    })
  }

  const undo = () => {
    data.editorCtx.undo({
      success: (res: any) => {
        data.undoTest = true
      },
      fail: (err: any) => {
        data.undoTest = false
      }
    })
  }

  const redo = () => {
    data.editorCtx.redo({
      success: (res: any) => {
        data.redoTest = true
      },
      fail: (err: any) => {
        data.redoTest = false
      }
    })
  }

  const format = (e: any) => {
    let { name, value } = e.target.dataset
    if (!name) return
    // console.log('format', name, value)
    data.editorCtx.format(name, value)
  }

  const onStatusChange = (e: any) => {
    const formats = e.detail
    data.formats = formats
  }

  const insertDivider = () => {
    data.editorCtx.insertDivider({
      success: function () {
        console.log('insert divider success')
      }
    })
  }

  const clear = () => {
    data.editorCtx.clear({
      success: function (res: any) {
        console.log("clear success",res)
      }
    })
  }

  const clearShowModal = () => {
    uni.showModal({
      title: '清空编辑器',
      content: '确定清空编辑器全部内容？',
      success: (res: any) => {
        if (res.confirm) {
          clear()
        }
      }
    })
  }

  const removeFormat = () => {
    data.editorCtx.removeFormat({
      success: (res: any) => {
        console.log('removeFormat-success', res)
        data.removeFormatTest = true
      },
      fail: (err: any) => {
        data.removeFormatTest = false
      }
    })
  }

  const insertDate = () => {
    const date = new Date()
    const formatDate = `${date.getFullYear()}/${date.getMonth() + 1}/${date.getDate()}`
    data.editorCtx.insertText({
      text: formatDate
    })
  }

  const insertImage = (image: string) => {
    data.editorCtx.insertImage({
      src: image,
      alt: '图像',
      success: () => {
        console.log('insert image success')
        data.insertImageTest = true
      }
    })
  }

  const chooseInsertImage = () => {
    uni.chooseImage({
      count: 1,
      success: (res: any) => {
        insertImage(res.tempFilePaths[0])
      }
    })
  }

  defineExpose({
    data,
    setContents,
    blur,
    clear,
    insertDivider,
    undo,
    redo,
    insertImage,
    removeFormat
  })
</script>

<style>
  @import "./editor-icon.css";

  .page-body {
    height: calc(100vh - var(--window-top) - var(--status-bar-height));
  }

  .wrapper {
    height: 100%;
  }

  .editor-wrapper {
    height: calc(100vh - var(--window-top) - var(--status-bar-height) - 140px - 46px);
    background: #fff;
  }

  .iconfont {
    display: inline-block;
    width: 30px;
    height: 30px;
    cursor: pointer;
    font-size: 20px;
    margin: 0px 6px;
    align-content: center;
  }

  .toolbar {
    box-sizing: border-box;
    border-bottom: 0;
    font-family: 'Helvetica Neue', 'Helvetica', 'Arial', sans-serif;
    flex-direction: row;
    flex-wrap: wrap;
    height: 140px;
    padding-left: 10px;
  }

  .ql-container {
    box-sizing: border-box;
    padding: 12px 15px;
    width: 100%;
    min-height: 30vh;
    height: 100%;
    font-size: 16px;
    line-height: 1.5;
  }

  .ql-active {
    color: #06c;
  }
</style>

```

